	$(".book-room-row").each(function(){
		$(".book-room-box").each(function(){
			var adultPax = 0;
			var childrenPax = 0;
			$(this).find(".adult-minus").click(function(e){
				if (adultPax <=0){
					e.preventDefault();
				} else {
					$(this).parent().parent().parent(".book-room-box").find(".adult-pax").text(--adultPax);
				}			
			});
			
			$(this).find(".adult-plus").click(function(e){
				if (adultPax > 3){
					e.preventDefault();
				} else {
					$(this).parent().parent().parent(".book-room-box").find(".adult-pax").text(++adultPax);
				}	
			});
			
			$(this).find(".children-minus").click(function(e){
				if (childrenPax <=0){
					e.preventDefault();
				} else {
					$(this).parent().parent().parent(".book-room-box").find(".children-pax").text(--childrenPax);
					if (childrenPax === 0){
						$(this).parent().parent().parent(".book-room-box").find(".bottom-book-room").slideUp();
					} else if (childrenPax === 1){
						$(this).parent().parent().parent(".book-room-box").find(".children-age-row:eq(1)").slideUp().addClass("none");
					}			
				}				
			});
			
			$(this).find(".children-plus").click(function(e){
				if (childrenPax > 1){
					e.preventDefault();
				} else {
					$(this).parent().parent().parent(".book-room-box").find(".children-pax").text(++childrenPax);
					if (childrenPax > 0){
						$(this).parent().parent().parent(".book-room-box").find(".bottom-book-room").slideDown();
					} 
					if (childrenPax === 2){
						$(this).parent().parent().parent(".book-room-box").find(".children-age-row:eq(1)").slideDown().removeClass("none");
					}
				}	
			});		
		});
	});

